// add-book.component.ts

import { Component } from '@angular/core';
import { BookService } from '../services/book.service';

@Component({
  selector: 'app-add-book',
  templateUrl: './add-book.component.html',
  styleUrls: ['./add-book.component.css'],
})
export class AddBookComponent {
  newBook :  = {
    titulo: '',
    autor: '',
    genero: '',
    libraryId: '',
  };

  newLibrary: Library = {
    nombre: '',
    direccion: '',
    ciudad: '',
  };

  constructor(private bookService: BookService) {}

  onSubmit() {
    this.bookService.createBook(this.newBook).subscribe(
      (response) => {
        console.log('Libro agregado con éxito:', response);
        // Limpiar el formulario o realizar otras acciones después de agregar el libro
      },
      (error) => {
        console.error('Error al agregar el libro:', error);
      }
    );
  }
}
<!-- add-book.component.html -->

<div>
  <h2>Agregar Libro</h2>
  <form (ngSubmit)="onSubmit()">
    <label for="titulo">Título:</label>
    <input
      type="text"
      id="titulo"
      name="titulo"
      [(ngModel)]="newBook.titulo"
      required
    />

    <label for="autor">Autor:</label>
    <input
      type="text"
      id="autor"
      name="autor"
      [(ngModel)]="newBook.autor"
      required
    />

    <label for="genero">Género:</label>
    <input
      type="text"
      id="genero"
      name="genero"
      [(ngModel)]="newBook.genero"
      required
    />

    <label for="libraryId">ID de la Biblioteca:</label>
    <input
      type="text"
      id="libraryId"
      name="libraryId"
      [(ngModel)]="newBook.libraryId"
      required
    />

    <button type="submit">Agregar Libro</button>
  </form>
</div>
